import  './Product.css';

function Product({ id, title, image,price,rating}) {
    return (
        <div className="product">
        <div className="product__info">
        <p>{title}</p>
            <p className= "product__price">
                <small>Rs</small>
                <strong>{price}</strong>
                </p>
        </div>
         <img src = {image} alt=""/>
            <button>Add to Cart</button>
            
        </div>
    )
}

export default Product
